import React from "react";



export default class Displaying extends React.Component{
  constructor(props) {
    super(props);
    };


    render(){
      return (
        <tr>
        <th scope="row">{this.props.sensor.sensorid}</th>
        <td>{this.props.sensor.sensorname}</td>
        <td>
        <div className="progress">
          <div className="progress-bar" role="progressbar" aria-valuenow={this.props.sensor.sensordata}
              aria-valuemin="0" aria-valuemax="100" style={{width:`${this.props.sensor.sensordata}%`}}>
              {this.props.sensor.sensordata} %
          </div>
        </div>        
        </td>
        <td>
                      <main class="main">
                <button id="random" class="button">Random value</button>

                <div class="gauge-container">
                  <div class="gauge"></div>

                </div>
              </main>



              <svg width="0" height="0" version="1.1" class="gradient-mask" xmlns="http://www.w3.org/2000/svg">
                <defs>
                    <linearGradient id="gradientGauge">
                      <stop class="color-red" offset="0%"/>
                      <stop class="color-yellow" offset="17%"/>
                      <stop class="color-green" offset="40%"/>
                      <stop class="color-yellow" offset="87%"/>
                      <stop class="color-red" offset="100%"/>
                    </linearGradient>
                </defs>  
              </svg>
        </td>

      </tr>
      );
}
}
