import React from "react";


const gaugeElement = document.querySelector(".gauge");


var guagedata = .9




export default class Displaying extends React.Component{
  constructor(props) {
    super(props);
    };
     setGaugeValue( value) {
      if (value < 0 || value > 1) {
        return;
      }

      gaugeElement.querySelector(".gauge__fill").style.transform = `rotate(${
        value / 2
      }turn)`;
      gaugeElement.querySelector(".gauge__cover").textContent = `${Math.round(
        value * 100
      )}%`;
    }

    render(){
      console.log(this.props.sensor.sensordata, 'this is it')
      this.setGaugeValue( this.props.sensor.sensordata/100);

      return (
        <tr>
