import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import Displaying from './displaying'

const Fetching = () => {
    const [actorsDatetime, setActorsDatetime] = useState('')
    const [actors, setActors] = useState(null)
    const [sensors, setSensors] = useState(null)
    const [userID] = useState(0) 
    const [loading , setLoading] = useState(true)
    const [sensorsDatetime, setSensorsDatetime] = useState('')                                           
    useEffect(()=>{
        const apiUrl1 = 'http://webswitch.ir:3000/actors'
        fetch (apiUrl1)
        .then ((response)=> response.json())
        .then  ((data1) => {setActors(data1.actors);
            setActorsDatetime(data1.datetime)            
        })
        console.log(actors)



        const apiUrl2 = 'http://webswitch.ir:3000/sensors'
        fetch (apiUrl2)
        .then ((response)=> response.json())
        .then  ((data2) => {setSensors(data2.sensors);
                            setSensorsDatetime(data2.datetime);
                            setLoading(false)});
        console.log (1)
    },[userID])
    if (loading){
        console.log(2);
        return(
            <h1>loading ....</h1>
        )};
    if (sensors) {
        return(<div>
                <table class="table table-striped">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name</th>
                    <th scope="col">Percentage</th>
                    </tr>
                </thead>
                <tbody>
                        {sensors.map((sensor,i) => <Displaying key={i} sensor={sensor}/>)}
                 </tbody>
                 </table>
                 <table class="table table-striped">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name</th>
                    <th scope="col">Switch</th>
                    </tr>
                </thead>
                <tbody>
                        {actors.map((actor,i) => <Switching key={i} actor={actor}/>)}
                 </tbody>
                 </table>
                 </div>

        )

    };

}

export default Fetching;
