import React from "react";
import { CircularGauge, Scale, Label, RangeContainer, Range, Title, Font, Export } from 'devextreme-react/circular-gauge';

export default class Displaying extends React.Component{
  constructor(props) {
    super(props);
    };


    render(){
      return (
        <tr>
        <th scope="row">{this.props.sensor.sensorid}</th>
        <td>{this.props.sensor.sensorname}</td>
        <td>{this.props.sensor.sensordata}</td>
        <td>
        <div className="progress">
          <div className="progress-bar" role="progressbar" aria-valuenow={this.props.sensor.sensordata}
              aria-valuemin="0" aria-valuemax="100" style={{width:`${this.props.sensor.sensordata}%`}}>
              {this.props.sensor.sensordata} %
          </div>
        </div>        
        </td>
        <td>
        <CircularGauge
        id="gauge"
        value={105}
      >
        <Scale startValue={50} endValue={150} tickInterval={10}>
          <Label useRangeColors={true} />
        </Scale>
        <RangeContainer palette="pastel">
          <Range startValue={50} endValue={90} />
          <Range startValue={90} endValue={130} />
          <Range startValue={130} endValue={150} />
        </RangeContainer>
        <Title text="Temperature of the Liquid in the Boiler">
          <Font size={28} />
        </Title>
        <Export enabled={true} />
      </CircularGauge>        </td>
      </tr>
      );
}
}
