import React from "react";

export default class Displaying extends React.Component{ 

    constructor(props) {
        super(props);
        this.state = {
            toggleVar: false
        }
        if (this.props.actor.actorstatus==1) {
            console.log('khodafez')
            this.toggleVar = true
            console.log(this.state.toggleVar)

        }else {
            this.toggleVar = false
            console.log(this.state.toggleVar)

        }
        }
        switchUpdate = (prevState) => {
            console.log(this.state.toggleVar)

            this.setState({toggleVar:!prevState.toggleVar})
            console.log(this.state.toggleVar)


        }

    render(){

    return (
        <tr>
        <th scope="row">{this.props.actor.actorid}</th>
        <td>{this.props.actor.actorname}</td>
        <td>
        <div className="form-check form-switch ">
  <input className="form-check-input " type="checkbox" id="flexSwitchCheckDefault" checked={this.state.toggleVar} onChange={this.switchUpdate}/>

</div>
        </td>

      </tr>
      );}}
